local getMapElements = function(attribute)
	local objects, index, tmpObject = { }, 0

	for data in str_gmatch(tfm.get.room.xmlMapInfo.xml, "<" .. attribute .. " (.-)/>") do
		index = index + 1
		objects[index] = { }
		tmpObject = objects[index]

